package TASKS;

public class DoubleComparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double x = 11.5;
		double y = 8.3;

		if (x > y) {
			System.out.println("Double value " + x + " is greater than double value " + y);

		} else
			System.out.println("Double value " + x + " is smaller than double value " + y);

	}

}
