package TASKS;

public class TemperatureCheck {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int temp = 25;

		if (temp < 32) {
			System.out.println("The water will freeze at " + temp + " degrees");

		} else
			System.out.println("The water will NOT freeze at " + temp + " degrees");

	}

}
